import { ChessBoard, ColumnString, RowNumber, Tile } from "../types";
import createTile from "./create-tile";
import findTileIndex from "./find-tile-index";

const movePiece = (board: ChessBoard, tile: Tile, newTile: [ColumnString, RowNumber]): ChessBoard => {
  const oldBoard = [...board];

  const [boardIndex, tileIndex] = findTileIndex(board, tile);

  // remove piece from old location
  const capturedRow = [...oldBoard[boardIndex!]];
  capturedRow.splice(tileIndex!, 1, createTile(tile.dark, tile.white, tile.tile));

  // @ts-ignore
  const newBoard = oldBoard.splice(boardIndex!, 1, capturedRow);

  // @ts-ignore
  return oldBoard;
};

export default movePiece;
