import React from "react";
import initializeBoard from "../helpers/initialize-board";
import Tile from "../components/Tile";

import "../css/ChessBoard.css";
import { ChessBoard, SelectedTile } from "../types";

interface Props { }

const boardArray: ChessBoard = initializeBoard();

export default (props: Props) => {

  const [selectedTile, setSelectedTile] = React.useState<SelectedTile>(undefined);
  const [moveTiles, setMoveTiles] = React.useState(undefined);

  const renderTile = (t: any) => {
    return <React.Fragment key={t.tile.join(",")}>
      <Tile tile={t}
        selectedTile={selectedTile}

        setSelectedTile={setSelectedTile}
        setMoveTiles={setMoveTiles}
      />
    </React.Fragment>
  }

  return <React.Fragment>
    <div className="container">
      {boardArray.map((c, i) => (
        <div className="row-container">
          A
          <div key={i} className="row">
            {c.map(renderTile)}
          </div>
        </div>
      ))}
    </div>
  </React.Fragment>;
};
