import { PieceID, ColumnString, RowNumber } from "../types";
import mathLetter from "./math-letter";

const getKingMoves = (tile: [ColumnString, RowNumber]) => {
  const moveArray = [];

  const l = tile[0];
  const n = tile[1];

  moveArray.push([mathLetter(l, "-"), n + 1]);
  moveArray.push([l, n + 1]);
  moveArray.push([mathLetter(l, "+"), n + 1]);
  moveArray.push([mathLetter(l, "-"), n]);
  moveArray.push([mathLetter(l, "+"), n]);
  moveArray.push([mathLetter(l, "-"), n - 1]);
  moveArray.push([l, n - 1]);
  moveArray.push([mathLetter(l, "+"), n - 1]);

  return moveArray.filter(t => (!!t[0] && !!t[1]));
};

const getPawnMoves = (tile: [ColumnString, RowNumber]) => { 
  const moveArray = [];

  const l = tile[0];
  const n = tile[1];

  moveArray.push([l, n + 1]);
  moveArray.push([l, n + 2]);

  return moveArray.filter(t => (!!t[0] && !!t[1]));
};

const getKnightMoves = (tile: [ColumnString, RowNumber]) => { };

const getBishopMoves = (tile: [ColumnString, RowNumber]) => { };

const getRookMoves = (tile: [ColumnString, RowNumber]) => { };

const getQueenMoves = (tile: [ColumnString, RowNumber]) => { };

const findMoveTiles = (piece: PieceID, tile: [ColumnString, RowNumber]) => {
  if (piece === 0) return getKingMoves(tile);
  if (piece === 1) return getPawnMoves(tile);
  if (piece === 2) return getKnightMoves(tile);
  if (piece === 3) return getBishopMoves(tile);
  if (piece === 4) return getRookMoves(tile);
  if (piece === 5) return getQueenMoves(tile);
}

export default findMoveTiles;
