import React from "react";
import { Tile, SelectedTile } from "../types";
import cn from "classnames";
import getTileAsset from "../helpers/get-tile-assets";
import findMoveTiles from "../helpers/find-move-tiles";

interface Props {
  tile: Tile;
  selectedTile: SelectedTile;
  setSelectedTile: any;
  setMoveTiles: any;
}

export default React.memo<Props>((props) => {

  const tile = props.tile;

  const tileAsset = getTileAsset(tile.piece);
  const hasPiece = !!tile.piece;
  const selected = props.selectedTile === tile.tile;
  const dark = (!selected && tile.dark);

  const handleClick = () => {
    if (!hasPiece) return;

    const moveTiles = findMoveTiles(tile.piece!, tile.tile);

    props.setSelectedTile(tile);
    props.setMoveTiles(moveTiles);
  }

  return <React.Fragment>
    <div className={cn("tile", { dark }, { selected }, { selectable: hasPiece })} onClick={handleClick}>
      <p>{tileAsset}</p>
    </div>
  </React.Fragment>;
});
